import logo from './logo.svg';
import './App.css';
import CO2 from "./CO2.gif";
import React, { useEffect, useState } from "react"; 
function App() {
  const [kilos, setKilos] = useState("");
  useEffect(() => 
  {
    setTimeout(() => setKilos(""), 10000);
  }, [kilos]);
  //{(parseFloat(kilos.replace(".", ",")) * 40).toFixed(2).toString().replace(".", ",")}
  console.log(parseFloat(kilos.replace(".", ",")))
  return (
    <div className="App">
      <header className="App-header">
        <div style={{position: "absolute", top: "5%", left: "49%", fontFamily:"roc-grotesk"}}>
          <p>
          <input name="user-name" type="string" placeholder="kilos" value={kilos} autoFocus onChange={(e) => { console.log(e.target.value);  console.log(typeof e.target.value); setKilos(e.target.value)}}/> 
          kg = {kilos !== "" ? (parseFloat(kilos.replace(".", ",")) * 40).toFixed(2).toString().replace(".", ","): 0.00} â‚¬
          </p>
        </div>
        <div>
          <img src={CO2} alt="CO2" width="100" height="100" style={{position: "absolute", top: "15%", left: "6%"}}/>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "12%"}}>{(kilos * 36.06).toFixed(2).toString().replace(".", ",")}</p>
          <p style={{position: "absolute", top: "59%", left: "15.6%", color: "white"}}>{(kilos * 196.39).toFixed(2).toString().replace(".", ",")}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "41%"}}>{(kilos * 110.10).toFixed(2).toString().replace(".", ",")}</p>
          <p style={{position: "absolute", top: "59%", left: "47.7%", color: "white"}}>{(kilos * 22.60).toFixed(2).toString().replace(".", ",")}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "72%"}}>{(kilos * 2403.85).toFixed(2).toString().replace(".", ",")}</p>
          <p style={{position: "absolute", top: "59%", left: "77.3%", color: "white"}}>{(kilos * 14.9).toFixed(2).toString().replace(".", ",")}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
