import logo from './logo.svg';
import './App.css';
import CO2 from "./CO2-bg-white.gif";
import joule from "./Joule-white-bg.gif";
import liter from "./Liter-white-bg.gif";

import React, { useEffect, useState } from "react"; 
function App() {
  const [kilos, setKilos] = useState("");
  useEffect(() => 
  {
    setTimeout(() => setKilos(""), 10000);
  }, [kilos]);
  //{(parseFloat(kilos.replace(".", ",")) * 40).toFixed(2).toString().replace(".", ",")}
  console.log(kilos);
  console.log(kilos.replace(",", "."))
  //console.log(e.target.value);  console.log(typeof e.target.value);
  //{{position: "absolute", top: "5%", left: "49%", fontFamily:"roc-grotesk"}}
  //position: "absolute", top: "25%", left: "8%"
  //style={{position: "absolute", top: "25%", left: "40%"}}
  //style={{position: "absolute", top: "25%", left: "70%"}}
  return (
    <div className="App">
      <header className="App-header">
        <div style={{position: "relative", top: 170, right: 50, fontFamily:"roc-grotesk"}}>
          <p>
          <input name="user-name" type="string" placeholder="kilos" value={kilos} autoFocus onChange={(e) => {  setKilos(e.target.value)}}/> 
          kg = {kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 40).toFixed(2).toString().replace(".", ","): 0.00} €
          </p>
        </div>
        <div style={{display: "flex", flexDirection: "row", justifyContent: "space-around", paddingTop: 60}}>
          <img src={CO2} alt="CO2" width="1000" height="1000" style={{}}/>
          <img src={joule} alt="CO2" width="1000" height="1000" />
          <img src={liter} alt="CO2" width="1000" height="1000" />

        </div>
        <div>
          <p style={{position: "absolute", fontSize: "5rem", bottom: "28%", left: "6%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 36.06).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute",fontSize: "5rem", bottom: "22.5%", left: "17.5%", color: "white"}}>{kilos !== "" ?(parseFloat(kilos.replace(",", ".")) * 196.39).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "41%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 110.10).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute", top: "59%", left: "47.7%", color: "white"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 22.60).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "72%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 2403.85).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute", top: "59%", left: "77.3%", color: "white"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 14.9).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
