import logo from './logo.svg';
import './App.css';
import CO2 from "./CO2-bg-white.gif";
import joule from "./Joule-white-bg.gif";
import liter from "./liter-white-bg.gif";

import React, { useEffect, useState } from "react"; 
function App() {
  const [kilos, setKilos] = useState("");
  useEffect(() => 
  {
    setTimeout(() => setKilos(""), 10000);
  }, [kilos]);
  //{(parseFloat(kilos.replace(".", ",")) * 40).toFixed(2).toString().replace(".", ",")}
  console.log(kilos);
  console.log(kilos.replace(",", "."))
  //console.log(e.target.value);  console.log(typeof e.target.value);
  return (
    <div className="App">
      <header className="App-header">
        <div style={{position: "absolute", top: "5%", left: "49%", fontFamily:"roc-grotesk"}}>
          <p>
          <input name="user-name" type="string" placeholder="kilos" value={kilos} autoFocus onChange={(e) => {  setKilos(e.target.value)}}/> 
          kg = {kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 40).toFixed(2).toString().replace(".", ","): 0.00} â‚¬
          </p>
        </div>
        <div>
          <img src={CO2} alt="CO2" width="250" height="250" style={{position: "absolute", top: "15%", left: "8%"}}/>
          <img src={joule} alt="CO2" width="250" height="250" style={{position: "absolute", top: "15%", left: "16%"}}/>

        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "12%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 36.06).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute", top: "59%", left: "15.6%", color: "white"}}>{kilos !== "" ?(parseFloat(kilos.replace(",", ".")) * 196.39).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "41%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 110.10).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute", top: "59%", left: "47.7%", color: "white"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 22.60).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
        <div>
          <p style={{position: "absolute", top: "53.5%", left: "72%"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 2403.85).toFixed(2).toString().replace(".", ","): 0.00}</p>
          <p style={{position: "absolute", top: "59%", left: "77.3%", color: "white"}}>{kilos !== "" ? (parseFloat(kilos.replace(",", ".")) * 14.9).toFixed(2).toString().replace(".", ","): 0.00}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
